import React, { Component } from "react";
import '../css/login.css'


export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
      }

      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
      }
    render () {
        return (
            <div className="dimeBody">
                <div className="dimeHeader">
                <div className="dimeImage">
                    <img src= {`icons/Logo-Dime-Hor 1.svg`} alt= "Dime logo"/>
                </div>
                <div className="dimeHeaderTwo">
                    <p>Why Dime</p>
                    <p>Products</p>
                    <p>Courses</p>
                    <p>Company</p>
                </div>
                </div>
                <div className="">
                    <div>
                        <h1>Sign in to your account</h1>
                        <h2>Enter your email and password to continue</h2>
                        <form>
                        <p for="fname">First name:</p> 
                            <input type="text" value={this.state.value} onChange={this.handleChange} placeholder="Enter your email address"/>

                        </form>
                    </div>
                    <div>

                    </div>
                </div>
            </div>
        )
    }
}
