import React, { Component } from "react";
import '../css/login.css'
import CustomButton from '../../library/CustomButton'

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
      }

      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
      }
    render () {
        return (
            <div className="dimeBody">
                <div className="dimeHeader">
                <div className="dimeImage">
                    <img src= {`icons/Logo-Dime-Hor 1.svg`} alt= "Dime logo"/>
                </div>
                <div className="dimeHeaderTwo">
                    <p>Why Dime</p>
                    <p>Products</p>
                    <p>Courses</p>
                    <p>Company</p>
                </div>
                </div>
                <div className="">
                    <div>
                        <h1>Sign in to your account</h1>
                        <h2>Enter your email and password to continue</h2>
                        <form>
                            <label for="email">Email Address</label> 
                            <input type="email" value={this.state.value} onChange={this.handleChange} placeholder="Enter your email address"/>

                            <label for="password">Password</label> 
                            <input type="password" value={this.state.value} onChange={this.handleChange} placeholder="Enter your password"/>

                            <div>
                                <div>
                                    <input type="checkbox" id="keep me signed in" name="signein" value="Bike"/>
                                    <label for="vehicle1"> Keep me signed in</label>
                                </div>
                                <h5>Forgot Details?</h5>
                            </div>
                        </form>
                        <CustomButton></CustomButton>
                    </div>
                    <div>

                    </div>
                </div>
            </div>
        )
    }
}
